int countWords(char* str1, char* str2) {    int counte = 0;    char* str1cpy = calloc(strlen(str1), sizeof(char));    if (!str1cpy)    {        printf("allocate fail!");        return 0;    }    else    {        strcpy(str1cpy, str1);        char* word = strtok(str1cpy, " ");        while (word != NULL)        {            if (strstr(word, str1cpy) != NULL)            {                counte++;            }            word = strtok(NULL, " ");        }        free(str1cpy);        return counte;    }}